() => {
  const [volume, setVolume] = React.useState(50);
  const [rotation, setRotation] = React.useState(0);

  const handleClick = () => {
    const randomJump = Math.random() * 40 - 20;
    const newVolume = Math.max(0, Math.min(100, volume + randomJump));
    setVolume(newVolume);
    setRotation(rotation + randomJump * 3);
  };

  return (
    <div style={{
      padding: '30px',
      background: '#1a1a1a',
      borderRadius: '15px',
      textAlign: 'center',
      cursor: 'pointer'
    }} onClick={handleClick}>
      <div style={{
        width: '120px',
        height: '120px',
        border: '8px solid #ff6b6b',
        borderRadius: '50%',
        margin: '0 auto 20px',
        position: 'relative',
        transform: `rotate(${rotation}deg)`,
        transition: 'transform 0.3s ease'
      }}>
        <div style={{
          width: '4px',
          height: '40px',
          background: '#fff',
          position: 'absolute',
          top: '10px',
          left: '50%',
          transform: 'translateX(-50%)'
        }} />
      </div>
      <div style={{
        color: '#fff',
        fontSize: '18px'
      }}>VOLUME: {Math.round(volume)}%</div>
      <div style={{
        color: '#999',
        fontSize: '12px',
        marginTop: '5px'
      }}>Click to adjust (good luck!)</div>
    </div>
  );
};

() => {
  const [volume, setVolume] = React.useState(50);
  const [position, setPosition] = React.useState({ x: 0, y: 0 });

  const handleMouseMove = (e) => {
    const rect = e.currentTarget.getBoundingClientRect();
    const mouseX = e.clientX - rect.left;
    const mouseY = e.clientY - rect.top;

    const centerX = rect.width / 2;
    const centerY = rect.height / 2;

    const deltaX = (mouseX - centerX) * 0.3;
    const deltaY = (mouseY - centerY) * 0.3;

    setPosition({ x: -deltaX, y: -deltaY });
  };

  const handleClick = () => {
    const newVolume = Math.random() * 100;
    setVolume(newVolume);
  };

  return (
    <div 
      style={{
        background: '#34495e',
        padding: '60px',
        borderRadius: '12px',
        position: 'relative',
        overflow: 'hidden',
        cursor: 'crosshair'
      }}
      onMouseMove={handleMouseMove}
    >
      <div style={{
        color: '#ecf0f1',
        textAlign: 'center',
        fontSize: '16px',
        marginBottom: '20px'
      }}>Try to click the slider!</div>

      <div 
        style={{
          width: '200px',
          height: '30px',
          background: '#95a5a6',
          borderRadius: '15px',
          position: 'relative',
          margin: '0 auto',
          transform: `translate(${position.x}px, ${position.y}px)`,
          transition: 'transform 0.1s ease-out',
          cursor: 'pointer'
        }}
        onClick={handleClick}
      >
        <div style={{
          width: `${volume}%`,
          height: '100%',
          background: 'linear-gradient(90deg, #e67e22, #f1c40f)',
          borderRadius: '15px',
          transition: 'width 0.3s ease'
        }} />
      </div>

      <div style={{
        color: '#ecf0f1',
        textAlign: 'center',
        fontSize: '16px',
        marginTop: '20px'
      }}>Volume: {Math.round(volume)}%</div>
    </div>
  );
};

